import React from "react";
import * as H from "history";
import styled from "styled-components";

import { useDispatch, useSelector } from "react-redux";
import { sampleSelector } from "../../redux/selectors/sampleSelector";
import sampleModule from "../../redux/modules/sampleModule";

import SampleButton from "../atoms/SampleButton";
import SampleInput from "../atoms/SampleInput";
import Button from 'react-bootstrap/Button';
import { Tab, Tabs } from 'react-bootstrap';
import { headerHeight } from "../../utils/size";


const HeaderBox = styled.div`
  height: ${headerHeight};
  display: flex;
  border-bottom:solid 1px #000;
  margin:0% 2%;
`;

const HeaderTextBox = styled.div`
  height: ${headerHeight};
  display: flex;
  font-size:20;
  margin-top:50px;
  margin-left:80px;

`;

const ContentBox = styled.div`
  margin-top:10%;
  width: 100%;
  background-color: #fff;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const TextLine=styled.div`
  background-color: #fff;
  display:flex;
`;

const Wrapper = styled.div`
  width: 100vw;
  height: 100vh;
`;

const Content = styled.div`
  width: 100%;
  height: calc(100% - ${headerHeight});
`;



const Home: React.FC = props => {
  // dispatch, state を用意
  const dispatch = useDispatch();
  const sampleState = useSelector(sampleSelector);

  const _handleClick = () => {
    dispatch(sampleModule.actions.changeNum(sampleState.someNum + 1));
  };
  const _handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(sampleModule.actions.changeStr(e.currentTarget.value));
  };
  const state={
    pageJudge:true,
    pages:""
  };

  if(state.pageJudge){
    return (
      <Wrapper>
        <HeaderBox>
        <div style={{fontSize:60}}>ID</div>
          <HeaderTextBox>
           <div style={{marginRight:20}}>Home</div>
           <div style={{marginRight:20}}>Profile</div>
           <div>Login</div>
          </HeaderTextBox>
        </HeaderBox>
        <Content>
          <ContentBox>
            <TextLine>
              <div>ログインID:</div>
              <SampleInput onChange={_handleChange} text={sampleState.someStr} />
            </ TextLine>
            <TextLine style={{margin:"1%"}}>
              <div >パスワード:</div>
              <SampleInput onChange={_handleChange} text={sampleState.someStr} />
            </ TextLine>
            <Button variant="light" style={{marginTop:"2%"}}>ログイン</Button>
          </ContentBox>
        </Content>  
      </Wrapper>
    );
  }

  else;{
    return(
      <ContentBox>Welcom to the ID</ContentBox>
    );
  }

};

export default Home;
